var PythonShell = require('python-shell');
var fs = require('fs');

var outputFilename = './image_data.json';

var options = {
    mode: 'text',
    pythonOptions: ['-u'],
    scriptPath: '/home/pi/pywork',
    args: []
};

module.exports={
    pics : function pics() {
        var shell = new PythonShell('shooting_test.py', options);
        shell.on('message', function (message) {
            json_file=message
            fs.writeFile(outputFilename, json_file, function(err) {
                if(err) {
                    console.log(err);
                } else {
                    console.log("JSON saved to " + outputFilename);
                }
            });

        });
    },

    one_pic : function one_pic(){
        var target
        var shell = new PythonShell('one_pic.py', options);
        shell.run('message', function(message){
           var target =message
        })
        return target;
    },


    pico : function pico(){
        return 1;
    }
}
